package com.test.abola;

import java.io.InputStream;
import java.net.URL;
import java.net.URLConnection;

public class ImageUtil {

	public static byte[] imgUrlgetByte(String imgUrl) {
		try {
			// 构造URL
			URL url = new URL(imgUrl);
			// 打开连接
			URLConnection con = url.openConnection();
			// 输入流
			InputStream in = con.getInputStream();
			byte[] guideImgContent = new byte[in.available()];
			in.read(guideImgContent);
			in.close();
			System.out.println("成功");
			return guideImgContent;
		} catch (Exception e) {
			// io發生意外回傳空直
			e.printStackTrace();
			return null;
		}
	}

	public static void main(String[] args) {

		String imgUrl = "data:image/jpeg;base64,";
		try {
			byte[] imgByte = ImageUtil.imgUrlgetByte(imgUrl);
			System.out.println(new String(imgByte, "utf-8"));
		} catch (Exception e) {
		}
	}

}
